import React, { Component } from "react";
import { Link } from "react-router-dom";
import LetterJ from "../../resources/pic/letterJ.png";
import { withRouter } from "react-router-dom";

class Header extends Component {
  state = {
    isScrollingDown: false,
    cursorPosition: 0,
    activeItem: "",
    items: [
      { to: "/", name: "home" },
      { to: "/portfolio", name: "portfolio" },
      { to: "/resume", name: "resume" }
    ]
  };

  componentDidMount() {
    const currentPath = this.props.location.pathname;
    const currentItem = this.state.items.find(item => item.to === currentPath);
    this.setState({ activeItem: currentItem.name });

    window.addEventListener("scroll", setTimeout(this.handleScroll, 30));
  }

  componentWillUnmount() {
    window.removeEventListener("scroll", this.handleScroll);
  }

  handleClick = name => {
    this.setState({ activeItem: name });
  };

  showHeaderItem = () =>
    this.state.items.map(item => (
      <Link key={item.name} to={item.to}>
        <div
          className={`header_button ${this.state.activeItem === item.name &&
            "active"}`}
          onClick={() => {
            this.handleClick(item.name);
          }}
        >
          {item.name}
        </div>
      </Link>
    ));

  render() {
    return (
      <header>
        <div className="header_container">
          <div className="header_logo">
            <Link to="/">
              <img src={LetterJ} alt="header logo" className="header_letter" />
            </Link>
          </div>
          {this.showHeaderItem()}
        </div>
      </header>
    );
  }
}

export default withRouter(Header);
