      <header id="home-top" class="nav-top nav-fixed nav-root">
        <div class="container">
          <div class="row">
            <div class="col-md-4 col-xs-4">
              <h1 id="header-logo" class="image-text">
                <a href="/">
                  <span class="mask"></span>
                  <span class="behind"></span>
                  Sean Halpin
                  <i class=""></i>
                </a>
              </h1>
            </div>

            <div class="col-md-8 col-xs-8">
              <nav id="top-nav">
                <ul class="nav-list transistion">
                  <li>
                    <a class="active" href="http://seanhalpin.io/">
                      About
                    </a>
                  </li>
                  <li>
                    <a href="http://seanhalpin.io/work">Work</a>
                  </li>
                  <li>
                    <a href="http://seanhalpin.io/notes">Notes</a>
                  </li>
                </ul>
              </nav>
            </div>
          </div>
        </div>
      </header>
    );
  }
}

export default Header;
