import React from "react";
import "./profileBox.css";
import profilePic from "../../../resources/pic/joey.JPG";
import Buttons from "../buttons";

const ProfileBox = props => {
  const { name, description, image, button } = props;
  console.log(button);
  return (
    <div className="profile_box">
      <div
        className="portfolio_container"
        // style={{ height: `${window.innerHeight}px` }}
      >
        <div className="left">
          <div className="left_container">
            <h2>{name}</h2>
            <p>{description}</p>
            <Buttons url={button[0].demoUrl} title="View Demo" />
            <Buttons url={button[0].github} title="View Source Code" />
          </div>
        </div>
        <div className="right">
          <div className="right_container">
            <img className="image" src={image} alt="profilePic" />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProfileBox;
