import React, { Component } from "react";
import "./contactForm.css";
import FormField from "../utils/formField";

const initialState = {
  // formError: null,
  formSuccess: null,
  formData: [
    (name: {
      element: "input",
      value: "",
      config: {
        name: "name",
        type: "text",
        placeholder: "Name"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    }),

    (email: {
      element: "input",
      value: "",
      config: {
        name: "email",
        type: "email",
        placeholder: "Email"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    }),

    (subject: {
      element: "input",
      value: "",
      config: {
        name: "subject",
        type: "text",
        placeholder: "Subject"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    }),

    (message: {
      element: "input",
      value: "",
      config: {
        name: "message",
        type: "text",
        placeholder: "Message"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    })
  ]
};

class ContactFrom extends Component {
  state = initialState;

  submitForm = event => {
    event.preventDefault();
  };

  updateForm = () => {};

  showFormField = () =>
    this.state.formData.map(item => (
      <FormField
        id={item.config.name}
        formData={item}
        change={this.updateForm}
      />
    ));

  render() {
    return (
      <div className="contact_form">
        <div className="form">
          <form onClick={this.submitForm}>
            {/* <FormField /> */}
            {this.showFormField()}
          </form>
        </div>
      </div>
    );
  }
}

export default ContactFrom;
