import React from "react";
import "./formField.css";

const FormField = ({ formData, change }) => {
  const showError = () => {
    let errMessage = "";

    if (!formData.valid)
      errMessage = (
        <div className="error_label">{formData.validationMessage}</div>
      );
    return errMessage;
  };

  const renderTemplate = () => {
    const id = formData.config.name;
    let formTemplate = null;
    switch (formData.element) {
      case "input":
        formTemplate = (
          <input
            {...formData.config}
            value={formData.value}
            onBlur={event => change({ event, id: { id }, blur: true })}
            onChange={event => change({ event, id: { id } })}
          />
        );
        break;
      case "textarea":
        formTemplate = (
          <textarea
            {...formData.config}
            value={formData.value}
            onBlur={event => change({ event, id: { id }, blur: true })}
            onChange={event => change({ event, id: { id } })}
          />
        );
        break;
      default:
        formTemplate = null;
    }
    return formTemplate;
  };

  return (
    <div className="formBlock">
      {renderTemplate()}
      {showError()}
    </div>
  );
};

export default FormField;
