import React, { Component } from "react";
import "./contactForm.css";
import FormField from "../utils/formField";
import { update } from "../utils/misc";

const initialState = {
  // formError: null,
  formSuccess: null,
  formData: {
    name: {
      element: "input",
      value: "",
      config: {
        name: "name",
        type: "text",
        placeholder: "Name"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    },

    email: {
      element: "input",
      value: "",
      config: {
        name: "email",
        type: "email",
        placeholder: "Email"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    },

    subject: {
      element: "input",
      value: "",
      config: {
        name: "subject",
        type: "text",
        placeholder: "Subject"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    },

    message: {
      element: "textarea",
      value: "",
      config: {
        name: "message",
        type: "text",
        placeholder: "Message"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    }
  }
};

class ContactFrom extends Component {
  state = initialState;

  submitForm = event => {
    event.preventDefault();
  };

  updateForm = element => {
    const formData = update(element, this.state.formData);
    this.setState({ formData });
  };

  showFormField = () => {
    const { formData } = this.state;
    return Object.keys(formData).map(key => (
      <FormField
        key={key}
        id={key}
        formData={formData[key]}
        change={this.updateForm}
      />
    ));
  };

  render() {
    return (
      <div className="contact_form">
        <div className="form">
          <form onClick={this.submitForm}>
            {this.showFormField()}
            <div className="button" onClick={this.submitForm}>
              Submit
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default ContactFrom;
