        name: "subject",
        type: "text",
        placeholder: "Subject"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    },

    message: {
      element: "input",
      value: "",
      config: {
        name: "message",
        type: "text",
        placeholder: "Message"
      },
      valid: false,
      // touched: false,
      validationMessage: ""
    }
