  render() {
    return (
      <div className="contact_form">
        <div className="form">
          <form onClick={this.submitForm}>
            {/* <FormField /> */}
            {this.showFormField()}
          </form>
        </div>
      </div>
    );
  }
}

export default ContactFrom;
