import React, { Component } from "react";
import "./intro.css";
import profilePic from "../../../resources/pic/joey.JPG";
import SkillBar from "./SkillBar";

class Intro extends Component {
  state = {
    skills: [
      { name: "Yython", width: 80 },
      { name: "JavasSript", width: 70 },
      { name: "CSS", width: 70 },
      { name: "React", width: 70 },
      { name: "NodeJS", width: 70 },
      { name: "MongoDB", width: 70 },
      { name: "Angular", width: 70 }
    ]
  };

  showSkills = () =>
    this.state.skills.map(skill => (
      <SkillBar key={skill.name} width={skill.width} name={skill.name} />
    ));

  render() {
    return (
      <div className="intro">
        <div className="split">
          <div className="left_container">
            <img className="profile" src={profilePic} alt="profile picture" />
            <h2>Xining (Joey) Wang</h2>
            <h3>Full Stack Developer</h3>
            <p>
              I am a developer based in Montreal, Canada. I have a passion for
              web development and algorithom.
            </p>
            <p>contact me for more detail</p>
          </div>
        </div>
        <div className="split">
          <div className="right_container">{this.showSkills()}</div>
        </div>
      </div>
    );
  }
}

export default Intro;
