import React, { Component } from "react";
import { Link } from "react-router-dom";
import LetterJ from "../../resources/pic/letterJ.png";
import { withRouter } from "react-router-dom";

class Header extends Component {
  state = {
    activeItem: "",
    items: [
      { to: "/", name: "home" },
      { to: "/portfolio", name: "portfolio" },
      { to: "/resume", name: "resume" }
    ]
  };

  componentDidMount() {
    const currentPath = this.props.location.pathname;
    for (let item in this.state.items) {
      if (item.to === currentPath) {
        return this.setState({ activeItem: item.name }).then(() =>
          this.showHeaderItem()
        );
      }
    }
  }

  handleClick = name => {
    this.setState({ activeItem: name });
  };

  showHeaderItem = () =>
    this.state.items.map(item => (
      <Link key={item.name} to={item.to}>
        <div
          className={`button ${this.state.activeItem === item.name &&
            "active"}`}
          onClick={() => {
            this.handleClick(item.name);
          }}
        >
          {item.name}
        </div>
      </Link>
    ));

  render() {
    console.log(this.props);
    return (
      <header>
        <div className="header_container">
          <div className="header_logo">
            <Link to="/">
              <img src={LetterJ} alt="header logo" className="header_letter" />
            </Link>
          </div>
          {this.showHeaderItem}
        </div>
      </header>
    );
  }
}

export default withRouter(Header);
